document.addEventListener("DOMContentLoaded",()=>{var e=new Swiper(".mySwiper",{spaceBetween:4,slidesPerView:3,freeMode:!0,watchSlidesProgress:!0,loop:!0});new Swiper(".mySwiper2",{spaceBetween:4,slidesPerView:1,navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},thumbs:{swiper:e},loop:!0,pagination:{el:".swiper-pagination",clickable:!0}})});const buttonAccordion=[...document.querySelectorAll(".accordion-title-box")];buttonAccordion.map(t=>{t.addEventListener("click",()=>{var e=t.nextElementSibling,i=t.querySelector(".accordion-arrow");e.classList.contains("accordion-text-visible")?(e.classList.remove("accordion-text-visible"),i.classList.remove("accordion-arrow_active")):(e.classList.add("accordion-text-visible"),i.classList.add("accordion-arrow_active"))})});