const buttonAccordion=[...document.querySelectorAll(".accordion-title-box")];buttonAccordion.map(i=>{i.addEventListener("click",()=>{var c=i.nextElementSibling,o=i.querySelector(".accordion-arrow");c.classList.contains("accordion-text-visible")?(c.classList.remove("accordion-text-visible"),o.classList.remove("accordion-arrow_active")):(c.classList.add("accordion-text-visible"),o.classList.add("accordion-arrow_active"))})});